import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { setupAuth, isAuthenticated } from "./replitAuth";
import { ObjectStorageService, ObjectNotFoundError } from "./objectStorage";
import { ObjectPermission } from "./objectAcl";
import { insertCategorySchema, insertArticleSchema, updateArticleSchema } from "@shared/schema";
import { z } from "zod";

export async function registerRoutes(app: Express): Promise<Server> {
  // Auth middleware
  await setupAuth(app);

  // Auth routes
  app.post('/api/auth/login', async (req, res) => {
    try {
      const { email, password } = req.body;

      if (!email || !password) {
        return res.status(400).json({ message: "Email e senha são obrigatórios" });
      }

      // Check if user exists
      const user = await storage.getUserByEmail(email);
      if (!user) {
        return res.status(401).json({ message: "Credenciais inválidas" });
      }

      // For demo purposes, we'll accept any password for existing users
      // In production, you'd verify the password hash
      if (!user.isActive) {
        return res.status(401).json({ message: "Conta desativada" });
      }

      // Create session (simplified for demo)
      (req.session as any).userId = user.id;
      (req.session as any).user = user;

      res.json({ 
        message: "Login realizado com sucesso",
        user: {
          id: user.id,
          email: user.email,
          firstName: user.firstName,
          lastName: user.lastName,
          role: user.role
        }
      });
    } catch (error) {
      console.error("Error during login:", error);
      res.status(500).json({ message: "Erro interno do servidor" });
    }
  });

  app.get('/api/auth/user', async (req: any, res) => {
    try {
      if (!(req.session as any)?.userId) {
        return res.status(401).json({ message: "Unauthorized" });
      }

      const user = await storage.getUser((req.session as any).userId);
      if (!user) {
        return res.status(401).json({ message: "Unauthorized" });
      }

      res.json(user);
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // Create a custom isAdmin middleware
  const isAdmin = async (req: any, res: any, next: any) => {
    try {
      if (!(req.session as any)?.userId) {
        return res.status(401).json({ message: "Login necessário" });
      }

      const user = await storage.getUser((req.session as any).userId);
      if (!user || !user.isActive) {
        return res.status(401).json({ message: "Usuário inválido" });
      }

      if (user.role !== 'admin') {
        return res.status(403).json({ message: "Acesso restrito apenas para administradores" });
      }

      req.user = user;
      next();
    } catch (error) {
      console.error("Error checking admin access:", error);
      res.status(500).json({ message: "Erro interno do servidor" });
    }
  };

  app.post('/api/auth/logout', (req, res) => {
    req.session.destroy((err) => {
      if (err) {
        return res.status(500).json({ message: "Erro ao fazer logout" });
      }
      res.json({ message: "Logout realizado com sucesso" });
    });
  });

  // Category routes
  app.get('/api/categories', async (req, res) => {
    try {
      const categories = await storage.getCategories();
      res.json(categories);
    } catch (error) {
      console.error("Error fetching categories:", error);
      res.status(500).json({ message: "Failed to fetch categories" });
    }
  });

  // Create a custom isAuthenticated middleware for our system
  const isAuthenticated = async (req: any, res: any, next: any) => {
    try {
      if (!(req.session as any)?.userId) {
        return res.status(401).json({ message: "Login necessário" });
      }

      const user = await storage.getUser((req.session as any).userId);
      if (!user || !user.isActive) {
        return res.status(401).json({ message: "Usuário inválido" });
      }

      req.user = user;
      next();
    } catch (error) {
      console.error("Error checking authentication:", error);
      res.status(500).json({ message: "Erro interno do servidor" });
    }
  };

  app.post('/api/categories', isAdmin, async (req: any, res) => {
    try {

      const categoryData = insertCategorySchema.parse(req.body);
      const category = await storage.createCategory(categoryData);
      res.status(201).json(category);
    } catch (error) {
      console.error("Error creating category:", error);
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid category data", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to create category" });
    }
  });

  app.put('/api/categories/:id', isAdmin, async (req: any, res) => {
    try {

      const categoryData = insertCategorySchema.partial().parse(req.body);
      const category = await storage.updateCategory(req.params.id, categoryData);

      if (!category) {
        return res.status(404).json({ message: "Category not found" });
      }

      res.json(category);
    } catch (error) {
      console.error("Error updating category:", error);
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid category data", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to update category" });
    }
  });

  app.delete('/api/categories/:id', isAdmin, async (req: any, res) => {
    try {

      const success = await storage.deleteCategory(req.params.id);
      if (!success) {
        return res.status(404).json({ message: "Category not found" });
      }

      res.status(204).send();
    } catch (error) {
      console.error("Error deleting category:", error);
      res.status(500).json({ message: "Failed to delete category" });
    }
  });

  // Article routes
  app.get('/api/articles', async (req, res) => {
    try {
      const { status, categoryId, authorId, search, limit, offset } = req.query;
      const articles = await storage.getArticles({
        status: status as string,
        categoryId: categoryId as string,
        authorId: authorId as string,
        search: search as string,
        limit: limit ? parseInt(limit as string) : undefined,
        offset: offset ? parseInt(offset as string) : undefined,
      });
      res.json(articles);
    } catch (error) {
      console.error("Error fetching articles:", error);
      res.status(500).json({ message: "Failed to fetch articles" });
    }
  });

  app.get('/api/articles/published', async (req, res) => {
    try {
      const { categoryId, limit, offset } = req.query;
      const articles = await storage.getPublishedArticles({
        categoryId: categoryId as string,
        limit: limit ? parseInt(limit as string) : undefined,
        offset: offset ? parseInt(offset as string) : undefined,
      });
      res.json(articles);
    } catch (error) {
      console.error("Error fetching published articles:", error);
      res.status(500).json({ message: "Failed to fetch published articles" });
    }
  });

  app.get('/api/articles/:id', async (req, res) => {
    try {
      const article = await storage.getArticleById(req.params.id);
      if (!article) {
        return res.status(404).json({ message: "Article not found" });
      }
      res.json(article);
    } catch (error) {
      console.error("Error fetching article:", error);
      res.status(500).json({ message: "Failed to fetch article" });
    }
  });

  app.get('/api/articles/slug/:slug', async (req, res) => {
    try {
      const article = await storage.getArticleBySlug(req.params.slug);
      if (!article) {
        return res.status(404).json({ message: "Article not found" });
      }

      // Increment view count
      await storage.incrementViewCount(article.id);

      res.json(article);
    } catch (error) {
      console.error("Error fetching article by slug:", error);
      res.status(500).json({ message: "Failed to fetch article" });
    }
  });

  app.post('/api/articles', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.id;

      const articleData = insertArticleSchema.parse({
        ...req.body,
        authorId: userId,
      });

      const article = await storage.createArticle(articleData);
      res.status(201).json(article);
    } catch (error) {
      console.error("Error creating article:", error);
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid article data", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to create article" });
    }
  });

  app.put('/api/articles/:id', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.id;

      // Check if user can edit this article
      const article = await storage.getArticleById(req.params.id);
      if (!article) {
        return res.status(404).json({ message: "Article not found" });
      }

      // Only admins or the article author can edit
      if (req.user.role !== 'admin' && article.authorId !== userId) {
        return res.status(403).json({ message: "Not authorized to edit this article" });
      }

      const articleData = updateArticleSchema.parse(req.body);
      const updatedArticle = await storage.updateArticle(req.params.id, articleData);

      if (!updatedArticle) {
        return res.status(404).json({ message: "Article not found" });
      }

      res.json(updatedArticle);
    } catch (error) {
      console.error("Error updating article:", error);
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid article data", errors: error.errors });
      }
      res.status(500).json({ message: "Failed to update article" });
    }
  });

  app.delete('/api/articles/:id', isAdmin, async (req: any, res) => {
    try {

      const success = await storage.deleteArticle(req.params.id);
      if (!success) {
        return res.status(404).json({ message: "Article not found" });
      }

      res.status(204).send();
    } catch (error) {
      console.error("Error deleting article:", error);
      res.status(500).json({ message: "Failed to delete article" });
    }
  });

  // Statistics route
  app.get('/api/stats', isAuthenticated, async (req: any, res) => {
    try {
      const stats = await storage.getStats();
      res.json(stats);
    } catch (error) {
      console.error("Error fetching stats:", error);
      res.status(500).json({ message: "Failed to fetch statistics" });
    }
  });

  // User management routes (Admin only)
  app.get('/api/users', isAdmin, async (req: any, res) => {
    try {
      const users = await storage.getAllUsers();
      res.json(users);
    } catch (error) {
      console.error("Error fetching users:", error);
      res.status(500).json({ message: "Failed to fetch users" });
    }
  });

  app.post('/api/users', isAdmin, async (req: any, res) => {
    try {
      const { email, firstName, lastName, role, password } = req.body;

      if (!email || !firstName || !lastName || !role) {
        return res.status(400).json({ message: "Todos os campos são obrigatórios" });
      }

      if (!['editor', 'journalist'].includes(role)) {
        return res.status(400).json({ message: "Papel inválido. Use 'editor' ou 'journalist'" });
      }

      // Check if user already exists
      const existingUser = await storage.getUserByEmail(email);
      if (existingUser) {
        return res.status(400).json({ message: "Usuário já existe com este email" });
      }

      const newUser = await storage.createUser({
        email,
        firstName,
        lastName,
        role,
        isActive: true
      });

      res.status(201).json({
        message: "Usuário criado com sucesso",
        user: {
          id: newUser.id,
          email: newUser.email,
          firstName: newUser.firstName,
          lastName: newUser.lastName,
          role: newUser.role,
          isActive: newUser.isActive
        }
      });
    } catch (error) {
      console.error("Error creating user:", error);
      res.status(500).json({ message: "Erro ao criar usuário" });
    }
  });

  app.put('/api/users/:id', isAdmin, async (req: any, res) => {
    try {
      const { id } = req.params;
      const { firstName, lastName, role, isActive } = req.body;

      const updatedUser = await storage.updateUser(id, {
        firstName,
        lastName,
        role,
        isActive
      });

      if (!updatedUser) {
        return res.status(404).json({ message: "Usuário não encontrado" });
      }

      res.json({
        message: "Usuário atualizado com sucesso",
        user: updatedUser
      });
    } catch (error) {
      console.error("Error updating user:", error);
      res.status(500).json({ message: "Erro ao atualizar usuário" });
    }
  });

  app.delete('/api/users/:id', isAdmin, async (req: any, res) => {
    try {
      const { id } = req.params;

      if (id === req.user.id) {
        return res.status(400).json({ message: "Não é possível deletar sua própria conta" });
      }

      const success = await storage.deleteUser(id);
      if (!success) {
        return res.status(404).json({ message: "Usuário não encontrado" });
      }

      res.json({ message: "Usuário deletado com sucesso" });
    } catch (error) {
      console.error("Error deleting user:", error);
      res.status(500).json({ message: "Erro ao deletar usuário" });
    }
  });

  // Object storage routes for image uploads
  app.post("/api/objects/upload", isAuthenticated, async (req, res) => {
    const objectStorageService = new ObjectStorageService();
    const uploadURL = await objectStorageService.getObjectEntityUploadURL();
    res.json({ uploadURL });
  });

  app.put("/api/article-images", isAuthenticated, async (req: any, res) => {
    if (!req.body.imageURL) {
      return res.status(400).json({ error: "imageURL is required" });
    }

    const userId = req.user.claims.sub;

    try {
      const objectStorageService = new ObjectStorageService();
      const objectPath = await objectStorageService.trySetObjectEntityAclPolicy(
        req.body.imageURL,
        {
          owner: userId,
          visibility: "public", // News images should be publicly accessible
        },
      );

      res.status(200).json({
        objectPath: objectPath,
      });
    } catch (error) {
      console.error("Error setting article image:", error);
      res.status(500).json({ error: "Internal server error" });
    }
  });

  app.get("/objects/:objectPath(*)", async (req, res) => {
    const objectStorageService = new ObjectStorageService();
    try {
      const objectFile = await objectStorageService.getObjectEntityFile(
        req.path,
      );
      objectStorageService.downloadObject(objectFile, res);
    } catch (error) {
      console.error("Error checking object access:", error);
      if (error instanceof ObjectNotFoundError) {
        return res.sendStatus(404);
      }
      return res.sendStatus(500);
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import {
  users,
  categories,
  articles,
  type User,
  type UpsertUser,
  type Category,
  type InsertCategory,
  type Article,
  type InsertArticle,
  type UpdateArticle,
  type ArticleWithRelations,
} from "@shared/schema";
import { db } from "./db";
import { eq, desc, asc, like, and, or, sql } from "drizzle-orm";

export interface IStorage {
  // User operations (required for Replit Auth)
  getUser(id: string): Promise<User | undefined>;
  getUserByEmail(email: string): Promise<User | undefined>;
  getAllUsers(): Promise<User[]>;
  createUser(user: Partial<UpsertUser>): Promise<User>;
  updateUser(id: string, user: Partial<UpsertUser>): Promise<User | undefined>;
  deleteUser(id: string): Promise<boolean>;
  upsertUser(user: UpsertUser): Promise<User>;

  // Category operations
  getCategories(): Promise<Category[]>;
  getCategoryById(id: string): Promise<Category | undefined>;
  getCategoryBySlug(slug: string): Promise<Category | undefined>;
  createCategory(category: InsertCategory): Promise<Category>;
  updateCategory(id: string, category: Partial<InsertCategory>): Promise<Category | undefined>;
  deleteCategory(id: string): Promise<boolean>;

  // Article operations
  getArticles(options?: {
    status?: string;
    categoryId?: string;
    authorId?: string;
    search?: string;
    limit?: number;
    offset?: number;
  }): Promise<ArticleWithRelations[]>;
  getArticleById(id: string): Promise<ArticleWithRelations | undefined>;
  getArticleBySlug(slug: string): Promise<ArticleWithRelations | undefined>;
  getPublishedArticles(options?: {
    categoryId?: string;
    limit?: number;
    offset?: number;
  }): Promise<ArticleWithRelations[]>;
  createArticle(article: InsertArticle): Promise<Article>;
  updateArticle(id: string, article: UpdateArticle): Promise<Article | undefined>;
  deleteArticle(id: string): Promise<boolean>;
  incrementViewCount(id: string): Promise<void>;

  // Statistics
  getStats(): Promise<{
    totalArticles: number;
    publishedArticles: number;
    draftArticles: number;
    totalUsers: number;
    totalViews: number;
  }>;
}

export class DatabaseStorage implements IStorage {
  // User operations
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async getUserByEmail(email: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.email, email));
    return user;
  }

  async getAllUsers(): Promise<User[]> {
    return await db.select().from(users).orderBy(asc(users.firstName));
  }

  async createUser(userData: Partial<UpsertUser>): Promise<User> {
    const [user] = await db
      .insert(users)
      .values({
        id: userData.id || crypto.randomUUID(),
        email: userData.email!,
        firstName: userData.firstName,
        lastName: userData.lastName,
        role: userData.role || 'editor',
        isActive: userData.isActive ?? true,
        profileImageUrl: userData.profileImageUrl,
        createdAt: new Date(),
        updatedAt: new Date()
      })
      .returning();
    return user;
  }

  async updateUser(id: string, userData: Partial<UpsertUser>): Promise<User | undefined> {
    const [user] = await db
      .update(users)
      .set({
        ...userData,
        updatedAt: new Date()
      })
      .where(eq(users.id, id))
      .returning();
    return user;
  }

  async deleteUser(id: string): Promise<boolean> {
    const result = await db.delete(users).where(eq(users.id, id));
    return (result.rowCount || 0) > 0;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  // Category operations
  async getCategories(): Promise<Category[]> {
    return await db.select().from(categories).orderBy(asc(categories.name));
  }

  async getCategoryById(id: string): Promise<Category | undefined> {
    const [category] = await db.select().from(categories).where(eq(categories.id, id));
    return category;
  }

  async getCategoryBySlug(slug: string): Promise<Category | undefined> {
    const [category] = await db.select().from(categories).where(eq(categories.slug, slug));
    return category;
  }

  async createCategory(category: InsertCategory): Promise<Category> {
    const [newCategory] = await db.insert(categories).values(category).returning();
    return newCategory;
  }

  async updateCategory(id: string, category: Partial<InsertCategory>): Promise<Category | undefined> {
    const [updated] = await db
      .update(categories)
      .set(category)
      .where(eq(categories.id, id))
      .returning();
    return updated;
  }

  async deleteCategory(id: string): Promise<boolean> {
    const result = await db.delete(categories).where(eq(categories.id, id));
    return (result.rowCount || 0) > 0;
  }

  // Article operations
  async getArticles(options: {
    status?: string;
    categoryId?: string;
    authorId?: string;
    search?: string;
    limit?: number;
    offset?: number;
  } = {}): Promise<ArticleWithRelations[]> {
    const { status, categoryId, authorId, search, limit = 50, offset = 0 } = options;

    let query = db
      .select()
      .from(articles)
      .leftJoin(categories, eq(articles.categoryId, categories.id))
      .leftJoin(users, eq(articles.authorId, users.id));

    const conditions = [];
    if (status) conditions.push(eq(articles.status, status as any));
    if (categoryId) conditions.push(eq(articles.categoryId, categoryId));
    if (authorId) conditions.push(eq(articles.authorId, authorId));
    if (search) {
      conditions.push(
        or(
          like(articles.title, `%${search}%`),
          like(articles.content, `%${search}%`)
        )
      );
    }

    const finalQuery = conditions.length > 0 ? query.where(and(...conditions)) : query;

    const results = await finalQuery
      .orderBy(desc(articles.createdAt))
      .limit(limit)
      .offset(offset);

    return results.map(result => ({
      ...result.articles,
      category: result.categories!,
      author: result.users!,
    }));
  }

  async getArticleById(id: string): Promise<ArticleWithRelations | undefined> {
    const [result] = await db
      .select()
      .from(articles)
      .leftJoin(categories, eq(articles.categoryId, categories.id))
      .leftJoin(users, eq(articles.authorId, users.id))
      .where(eq(articles.id, id));

    if (!result) return undefined;

    return {
      ...result.articles,
      category: result.categories!,
      author: result.users!,
    };
  }

  async getArticleBySlug(slug: string): Promise<ArticleWithRelations | undefined> {
    const [result] = await db
      .select()
      .from(articles)
      .leftJoin(categories, eq(articles.categoryId, categories.id))
      .leftJoin(users, eq(articles.authorId, users.id))
      .where(eq(articles.slug, slug));

    if (!result) return undefined;

    return {
      ...result.articles,
      category: result.categories!,
      author: result.users!,
    };
  }

  async getPublishedArticles(options: {
    categoryId?: string;
    limit?: number;
    offset?: number;
  } = {}): Promise<ArticleWithRelations[]> {
    return this.getArticles({
      ...options,
      status: "published",
    });
  }

  async createArticle(article: InsertArticle): Promise<Article> {
    // Generate slug from title
    const slug = article.title
      .toLowerCase()
      .replace(/[^a-z0-9]+/g, '-')
      .replace(/(^-|-$)/g, '');

    const [newArticle] = await db
      .insert(articles)
      .values({
        ...article,
        slug,
      })
      .returning();
    return newArticle;
  }

  async updateArticle(id: string, article: UpdateArticle): Promise<Article | undefined> {
    const updateData: any = { ...article, updatedAt: new Date() };

    // Update slug if title changed
    if (article.title) {
      updateData.slug = article.title
        .toLowerCase()
        .replace(/[^a-z0-9]+/g, '-')
        .replace(/(^-|-$)/g, '');
    }

    // Set publishedAt when status changes to published
    if (article.status === 'published' && !article.publishedAt) {
      updateData.publishedAt = new Date();
    }

    const [updated] = await db
      .update(articles)
      .set(updateData)
      .where(eq(articles.id, id))
      .returning();
    return updated;
  }

  async deleteArticle(id: string): Promise<boolean> {
    const result = await db.delete(articles).where(eq(articles.id, id));
    return (result.rowCount || 0) > 0;
  }

  async incrementViewCount(id: string): Promise<void> {
    await db
      .update(articles)
      .set({ viewCount: sql`${articles.viewCount} + 1` })
      .where(eq(articles.id, id));
  }

  // Statistics
  async getStats(): Promise<{
    totalArticles: number;
    publishedArticles: number;
    draftArticles: number;
    totalUsers: number;
    totalViews: number;
  }> {
    const [totalArticlesResult] = await db
      .select({ count: sql<number>`count(*)::int` })
      .from(articles);

    const [publishedArticlesResult] = await db
      .select({ count: sql<number>`count(*)::int` })
      .from(articles)
      .where(eq(articles.status, 'published'));

    const [draftArticlesResult] = await db
      .select({ count: sql<number>`count(*)::int` })
      .from(articles)
      .where(eq(articles.status, 'draft'));

    const [totalUsersResult] = await db
      .select({ count: sql<number>`count(*)::int` })
      .from(users);

    const [totalViewsResult] = await db
      .select({ sum: sql<number>`coalesce(sum(${articles.viewCount}), 0)::int` })
      .from(articles);

    return {
      totalArticles: totalArticlesResult.count,
      publishedArticles: publishedArticlesResult.count,
      draftArticles: draftArticlesResult.count,
      totalUsers: totalUsersResult.count,
      totalViews: totalViewsResult.sum,
    };
  }
}

export const storage = new DatabaseStorage();

