client/src/components/News/NewsCard.tsx.content:
import { Link } from "wouter";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";

interface NewsCardProps {
  article: any;
  showActions?: boolean;
  onEdit?: (id: string) => void;
  onDelete?: (id: string) => void;
  onToggleStatus?: (id: string, status: string) => void;
}

export default function NewsCard({ 
  article, 
  showActions = true, 
  onEdit, 
  onDelete, 
  onToggleStatus 
}: NewsCardProps) {
  const getStatusColor = (status: string) => {
    switch (status) {
      case 'published': return 'bg-green-100 text-green-800';
      case 'draft': return 'bg-yellow-100 text-yellow-800';
      case 'review': return 'bg-blue-100 text-blue-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getStatusText = (status: string) => {
    switch (status) {
      case 'published': return 'Publicado';
      case 'draft': return 'Rascunho';
      case 'review': return 'Em Revisão';
      default: return status;
    }
  };

  return (
    <Card className="hover:shadow-md transition-shadow" data-testid={`card-news-${article.id}`}>
      <CardContent className="p-4">
        <div className="flex items-start space-x-4">
          {article.imageUrl && (
            <img
              src={article.imageUrl}
              alt={article.title}
              className="w-16 h-10 object-cover rounded"
            />
          )}
          <div className="flex-1">
            <div className="flex items-start justify-between">
              <div>
                <h3 className="font-medium text-dark-blue mb-1" data-testid={`text-article-title-${article.id}`}>
                  {article.title}
                </h3>
                <p className="text-sm text-gray-500 mb-2">
                  <span data-testid={`text-article-author-${article.id}`}>
                    Por: {article.author?.firstName} {article.author?.lastName}
                  </span> • {" "}
                  <span data-testid={`text-article-date-${article.id}`}>
                    {new Date(article.createdAt).toLocaleDateString('pt-BR')}
                  </span>
                </p>
              </div>
              <div className="flex items-center space-x-2">
                <span className={`px-2 py-1 text-xs font-medium rounded-full ${getStatusColor(article.status)}`}>
                  {getStatusText(article.status)}
                </span>
                {showActions && (
                  <div className="flex space-x-1">
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => onEdit?.(article.id)}
                      data-testid={`button-edit-${article.id}`}
                    >
                      <i className="fas fa-edit"></i>
                    </Button>
                    {onToggleStatus && (
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => onToggleStatus(article.id, article.status)}
                        data-testid={`button-toggle-status-${article.id}`}
                      >
                        <i className={`fas ${article.status === 'published' ? 'fa-eye-slash' : 'fa-eye'}`}></i>
                      </Button>
                    )}
                    {onDelete && (
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => onDelete(article.id)}
                        className="text-red-600 hover:text-red-800"
                        data-testid={`button-delete-${article.id}`}
                      >
                        <i className="fas fa-trash"></i>
                      </Button>
                    )}
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/News/NewsForm.tsx.content:
import { useState, useEffect } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import RichTextEditor from "./RichTextEditor";
import { ObjectUploader } from "@/components/ObjectUploader";
import { apiRequest } from "@/lib/queryClient";

const articleSchema = z.object({
  title: z.string().min(1, "Título é obrigatório"),
  excerpt: z.string().optional(),
  content: z.string().min(1, "Conteúdo é obrigatório"),
  categoryId: z.string().min(1, "Categoria é obrigatória"),
  imageUrl: z.string().optional(),
  status: z.enum(["draft", "review", "published"]).default("draft"),
  tags: z.string().optional(),
});

type ArticleFormData = z.infer<typeof articleSchema>;

interface NewsFormProps {
  initialData?: any;
  categories: any[];
  onSubmit: (data: ArticleFormData) => void;
  onCancel: () => void;
  isLoading?: boolean;
}

export default function NewsForm({ 
  initialData, 
  categories, 
  onSubmit, 
  onCancel, 
  isLoading = false 
}: NewsFormProps) {
  const [imageUrl, setImageUrl] = useState(initialData?.imageUrl || "");

  const form = useForm<ArticleFormData>({
    resolver: zodResolver(articleSchema),
    defaultValues: {
      title: initialData?.title || "",
      excerpt: initialData?.excerpt || "",
      content: initialData?.content || "",
      categoryId: initialData?.categoryId || "",
      imageUrl: initialData?.imageUrl || "",
      status: initialData?.status || "draft",
      tags: initialData?.tags || "",
    },
  });

  const handleImageUpload = async () => {
    try {
      const response = await apiRequest("POST", "/api/objects/upload");
      const data = await response.json();
      return {
        method: "PUT" as const,
        url: data.uploadURL,
      };
    } catch (error) {
      console.error("Error getting upload URL:", error);
      throw error;
    }
  };

  const handleImageComplete = async (result: any) => {
    if (result.successful && result.successful.length > 0) {
      const uploadedFile = result.successful[0];
      try {
        const response = await apiRequest("PUT", "/api/article-images", {
          imageURL: uploadedFile.uploadURL,
        });
        const data = await response.json();
        setImageUrl(data.objectPath);
        form.setValue("imageUrl", data.objectPath);
      } catch (error) {
        console.error("Error setting image ACL:", error);
      }
    }
  };

  const handleSubmit = (data: ArticleFormData) => {
    onSubmit({
      ...data,
      imageUrl: imageUrl || undefined,
    });
  };

  return (
    <Card>
      <CardContent className="p-6">
        <Form {...form}>
          <form onSubmit={form.handleSubmit(handleSubmit)} className="space-y-6">
            {/* Basic Information */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <FormField
                control={form.control}
                name="title"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Título *</FormLabel>
                    <FormControl>
                      <Input placeholder="Digite o título da notícia..." {...field} data-testid="input-title" />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
              <FormField
                control={form.control}
                name="categoryId"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Categoria *</FormLabel>
                    <Select onValueChange={field.onChange} value={field.value}>
                      <FormControl>
                        <SelectTrigger data-testid="select-category">
                          <SelectValue placeholder="Selecione uma categoria" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        {categories.map((category) => (
                          <SelectItem key={category.id} value={category.id}>
                            {category.name}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <FormField
              control={form.control}
              name="excerpt"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Resumo/Subtítulo</FormLabel>
                  <FormControl>
                    <Textarea 
                      placeholder="Breve resumo da notícia..." 
                      rows={3} 
                      {...field} 
                      data-testid="textarea-excerpt"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            {/* Image Upload */}
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Imagem Principal</label>
              <div className="space-y-4">
                <ObjectUploader
                  maxNumberOfFiles={1}
                  maxFileSize={10485760}
                  onGetUploadParameters={handleImageUpload}
                  onComplete={handleImageComplete}
                  buttonClassName="w-full border-2 border-dashed border-gray-300 rounded-lg p-6 hover:border-secondary-blue transition-colors"
                >
                  <div className="text-center space-y-4">
                    <i className="fas fa-cloud-upload-alt text-4xl text-gray-400"></i>
                    <div>
                      <p className="text-gray-600">Arraste e solte uma imagem aqui ou</p>
                      <span className="text-secondary-blue font-medium hover:text-blue-700">
                        clique para selecionar
                      </span>
                    </div>
                    <p className="text-xs text-gray-500">PNG, JPG, WEBP até 10MB</p>
                  </div>
                </ObjectUploader>

                {imageUrl && (
                  <div className="mt-4">
                    <img 
                      src={imageUrl} 
                      alt="Preview" 
                      className="max-w-xs rounded-lg"
                      data-testid="img-preview"
                    />
                  </div>
                )}
              </div>
            </div>

            {/* Content Editor */}
            <FormField
              control={form.control}
              name="content"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Conteúdo da Notícia *</FormLabel>
                  <FormControl>
                    <RichTextEditor
                      value={field.value}
                      onChange={field.onChange}
                      placeholder="Digite o conteúdo da notícia..."
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            {/* Publishing Options */}
            <div className="bg-gray-50 rounded-lg p-6">
              <h3 className="text-lg font-medium text-dark-blue mb-4">Opções de Publicação</h3>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <FormField
                  control={form.control}
                  name="status"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Status</FormLabel>
                      <Select onValueChange={field.onChange} value={field.value}>
                        <FormControl>
                          <SelectTrigger data-testid="select-status">
                            <SelectValue />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          <SelectItem value="draft">Rascunho</SelectItem>
                          <SelectItem value="review">Em Revisão</SelectItem>
                          <SelectItem value="published">Publicado</SelectItem>
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <FormField
                  control={form.control}
                  name="tags"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Tags</FormLabel>
                      <FormControl>
                        <Input 
                          placeholder="política, segurança, local" 
                          {...field} 
                          data-testid="input-tags"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              </div>
            </div>

            {/* Action Buttons */}
            <div className="flex justify-end space-x-4 pt-6 border-t">
              <Button
                type="button"
                variant="outline"
                onClick={onCancel}
                disabled={isLoading}
                data-testid="button-cancel"
              >
                Cancelar
              </Button>
              <Button
                type="submit"
                className="bg-secondary-blue hover:bg-blue-600 text-white"
                disabled={isLoading}
                data-testid="button-submit"
              >
                {isLoading ? (
                  <>
                    <i className="fas fa-spinner fa-spin mr-2"></i>
                    Salvando...
                  </>
                ) : (
                  <>
                    <i className="fas fa-save mr-2"></i>
                    Salvar Notícia
                  </>
                )}
              </Button>
            </div>
          </form>
        </Form>
      </CardContent>
    </Card>
  );
}

client/src/components/News/RichTextEdito.content:
import { useState, useRef, useEffect } from "react";

interface RichTextEditorProps {
  value: string;
  onChange: (value: string) => void;
  placeholder?: string;
}

export default function RichTextEditor({ value, onChange, placeholder }: RichTextEditorProps) {
  const editorRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    if (editorRef.current && editorRef.current.innerHTML !== value) {
      editorRef.current.innerHTML = value;
    }
  }, [value]);

  const handleInput = () => {
    if (editorRef.current) {
      onChange(editorRef.current.innerHTML);
    }
  };

  const executeCommand = (command: string, value?: string) => {
    document.execCommand(command, false, value);
    editorRef.current?.focus();
  };

  const insertLink = () => {
    const url = prompt("Digite a URL:");
    if (url) {
      executeCommand("createLink", url);
    }
  };

  return (
    <div className="border border-gray-300 rounded-lg overflow-hidden">
      {/* Toolbar */}
      <div className="border-b p-2 flex flex-wrap gap-2 bg-gray-50">
        <button
          type="button"
          className="p-2 hover:bg-gray-200 rounded"
          onClick={() => executeCommand("bold")}
          data-testid="button-bold"
        >
          <i className="fas fa-bold"></i>
        </button>
        <button
          type="button"
          className="p-2 hover:bg-gray-200 rounded"
          onClick={() => executeCommand("italic")}
          data-testid="button-italic"
        >
          <i className="fas fa-italic"></i>
        </button>
        <button
          type="button"
          className="p-2 hover:bg-gray-200 rounded"
          onClick={() => executeCommand("underline")}
          data-testid="button-underline"
        >
          <i className="fas fa-underline"></i>
        </button>

        <div className="w-px bg-gray-300"></div>

        <button
          type="button"
          className="p-2 hover:bg-gray-200 rounded"
          onClick={() => executeCommand("formatBlock", "h1")}
          data-testid="button-h1"
        >
          <i className="fas fa-heading"></i>1
        </button>
        <button
          type="button"
          className="p-2 hover:bg-gray-200 rounded"
          onClick={() => executeCommand("formatBlock", "h2")}
          data-testid="button-h2"
        >
          <i className="fas fa-heading"></i>2
        </button>

        <div className="w-px bg-gray-300"></div>

        <button
          type="button"
          className="p-2 hover:bg-gray-200 rounded"
          onClick={insertLink}
          data-testid="button-link"
        >
          <i className="fas fa-link"></i>
        </button>
        <button
          type="button"
          className="p-2 hover:bg-gray-200 rounded"
          onClick={() => executeCommand("insertOrderedList")}
          data-testid="button-ordered-list"
        >
          <i className="fas fa-list-ol"></i>
        </button>
        <button
          type="button"
          className="p-2 hover:bg-gray-200 rounded"
          onClick={() => executeCommand("insertUnorderedList")}
          data-testid="button-unordered-list"
        >
          <i className="fas fa-list-ul"></i>
        </button>
      </div>

      {/* Editor Content */}
      <div
        ref={editorRef}
        className="p-4 min-h-64 focus:outline-none focus:ring-2 focus:ring-secondary-blue"
        contentEditable
        onInput={handleInput}
        dangerouslySetInnerHTML={{ __html: value }}
        data-placeholder={placeholder}
        data-testid="editor-content"
        style={{
          whiteSpace: "pre-wrap",
        }}
      />
    </div>
  );
}

